import React from "react";
import {useState} from "react";
import './App.css';

const Square = () => {
  const [value, setValue] = useState(null)
  function handleClick() {
    setValue("X")
  }

  return (
    <button 
      className="square"
      onClick={handleClick}
    >
      {value}  
    </button> 
  );
};

const Board = () => {
  return (
    <div className="container">
      <h1>Jogo da Velha</h1>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div> 
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>        
    </div>
  );
};

export default Board;
